mapper.insert:
INSERT INTO users (
    user_id, email, name, password, create_at
) VALUES (
    #{userId},
    #{email},
    #{name},
    #{password},
    #{createAt}
)

mapper.select:
SELECT user_id, email, name, password, create_at
FROM users
WHERE user_id = #{userId}

SELECT user_id, email, name, password, create_at
FROM users
WHERE email = #{email}

mapper.update:
UPDATE users SET
    email = #{email},
    name = #{name},
    password = #{password}
WHERE user_id = #{userId}

mapper.select:
SELECT COUNT(*)
FROM users
WHERE email = #{email}

SELECT COUNT(*)
FROM users
WHERE email = #{email}
AND user_id != #{excludeUserId}


SELECT * FROM (
    SELECT ROWNUM rn, a.*
    FROM (
        SELECT user_id, email, name, password, create_at
        FROM users
        WHERE name LIKE '%' || #{name} || '%'
        ORDER BY create_at DESC
    ) a
    WHERE ROWNUM <= #{offset} + #{limit}
)
WHERE rn > #{offset}
